import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { CommonModule } from '@angular/common';
import { PatientService } from '../../../core/services/patient.service';
import { AiService } from '../../../core/services/ai.service';
import { PatientDetail } from '../../../core/models/patient.model';
import { AiPrediction } from '../../../core/models/prediction.model';

@Component({
  selector: 'app-patient-detail',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './patient-detail.component.html',
  styleUrls: ['./patient-detail.component.css']
})
export class PatientDetailComponent implements OnInit {
  patient: PatientDetail | null = null;
  prediction: AiPrediction | null = null;
  loading = false;
  error = '';
  patientId: number = 0;

  constructor(
    private patientService: PatientService,
    private aiService: AiService,
    private route: ActivatedRoute,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.route.params.subscribe(params => {
      this.patientId = params['id'];
      this.loadPatient();
      this.loadPrediction();
    });
  }

  loadPatient(): void {
    this.loading = true;
    this.error = '';
    this.patientService.getById(this.patientId).subscribe({
      next: (data) => {
        this.patient = data;
        this.loading = false;
      },
      error: () => {
        this.error = 'Hasta bilgileri y�klenirken hata olu�tu.';
        this.loading = false;
      }
    });
  }

  loadPrediction(): void {
    this.aiService.getPrediction(this.patientId).subscribe({
      next: (data) => {
        this.prediction = data;
      },
      error: () => {
        // AI tahminini alamazsa sessiz kalan hata
      }
    });
  }

  goBack(): void {
    this.router.navigate(['/patients']);
  }
}
<div class="container" *ngIf="!loading">
  <div class="header">
    <button class="btn btn-back" (click)="goBack()">? Geri</button>
    <h1 *ngIf="patient">{{ patient.name }} {{ patient.surname }}</h1>
  </div>

  <div *ngIf="error" class="alert alert-danger">
    {{ error }}
  </div>

  <div *ngIf="patient" class="detail-card">
    <div class="section">
      <h2>Hasta Bilgileri</h2>
      <div class="info-grid">
        <div class="info-item">
          <label>Ad:</label>
          <p>{{ patient.name }}</p>
        </div>
        <div class="info-item">
          <label>Soyad�:</label>
          <p>{{ patient.surname }}</p>
        </div>
        <div class="info-item">
          <label>Do�um Tarihi:</label>
          <p>{{ patient.birthDate | date: 'dd.MM.yyyy' }}</p>
        </div>
      </div>
    </div>

    <div class="section" *ngIf="prediction">
      <h2>AI Tahminlemesi</h2>
      <div class="prediction-box">
        <div class="prediction-item">
          <label>Risk Seviyesi:</label>
          <p class="prediction-value">{{ prediction.prediction }}</p>
        </div>
        <div class="prediction-item">
          <label>G�ven Oran�:</label>
          <p class="prediction-value">{{ (prediction.confidence * 100).toFixed(2) }}%</p>
        </div>
      </div>
    </div>

    <div class="section" *ngIf="patient.records && patient.records.length > 0">
      <h2>T�bbi Kay�tlar</h2>
      <div class="records-list">
        <div *ngFor="let record of patient.records" class="record-item">
          <div class="record-header">
            <span class="date">{{ record.date | date: 'dd.MM.yyyy HH:mm' }}</span>
          </div>
          <div class="record-content">
            <div class="record-field">
              <label>Tan�:</label>
              <p>{{ record.diagnosis }}</p>
            </div>
            <div class="record-field">
              <label>Doktor Notlar�:</label>
              <p>{{ record.doctorRemarks }}</p>
            </div>
          </div>
        </div>
      </div>
    </div>

    <div class="section" *ngIf="!patient.records || patient.records.length === 0">
      <p class="empty">T�bbi kay�t bulunmamaktad�r.</p>
    </div>
  </div>
</div>

<div class="container loading" *ngIf="loading">
  Y�kleniyor...
</div>
