import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators, ReactiveFormsModule } from '@angular/forms';
import { Router } from '@angular/router';
import { CommonModule } from '@angular/common';
import { PatientService } from '../../../core/services/patient.service';
import { CreatePatientRequest } from '../../../core/models/patient.model';

@Component({
  selector: 'app-patient-create',
  standalone: true,
  imports: [CommonModule, ReactiveFormsModule],
  templateUrl: './patient-create.component.html',
  styleUrls: ['./patient-create.component.css']
})
export class PatientCreateComponent {
  patientForm: FormGroup;
  loading = false;
  submitted = false;
  error = '';

  constructor(
    private fb: FormBuilder,
    private patientService: PatientService,
    private router: Router
  ) {
    this.patientForm = this.fb.group({
      name: ['', Validators.required],
      surname: ['', Validators.required],
      birthDate: ['', Validators.required]
    });
  }

  get f() {
    return this.patientForm.controls;
  }

  onSubmit(): void {
    this.submitted = true;
    this.error = '';

    if (this.patientForm.invalid) {
      return;
    }

    this.loading = true;
    const request: CreatePatientRequest = {
      name: this.patientForm.value.name,
      surname: this.patientForm.value.surname,
      birthDate: new Date(this.patientForm.value.birthDate)
    };

    this.patientService.create(request).subscribe({
      next: (patient) => {
        this.router.navigate(['/patients', patient.id]);
      },
      error: (error: any) => {
        this.error = 'Hasta eklenirken hata oluştu.';
        this.loading = false;
      }
    });
  }

  goBack(): void {
    this.router.navigate(['/patients']);
  }
}
<div class="container">
  <div class="header">
    <button class="btn btn-back" (click)="goBack()">← Geri</button>
    <h1>Yeni Hasta Ekle</h1>
  </div>

  <div class="form-card">
    <form [formGroup]="patientForm" (ngSubmit)="onSubmit()">
      <div class="form-group">
        <label for="name">Ad *</label>
        <input
          id="name"
          type="text"
          class="form-control"
          formControlName="name"
          [class.is-invalid]="submitted && f['name'].errors"
          placeholder="Hasta adını girin"
        />
        <div *ngIf="submitted && f['name'].errors" class="invalid-feedback">
          Ad gereklidir
        </div>
      </div>

      <div class="form-group">
        <label for="surname">Soyadı *</label>
        <input
          id="surname"
          type="text"
          class="form-control"
          formControlName="surname"
          [class.is-invalid]="submitted && f['surname'].errors"
          placeholder="Hasta soyadını girin"
        />
        <div *ngIf="submitted && f['surname'].errors" class="invalid-feedback">
          Soyadı gereklidir
        </div>
      </div>

      <div class="form-group">
        <label for="birthDate">Doğum Tarihi *</label>
        <input
          id="birthDate"
          type="date"
          class="form-control"
          formControlName="birthDate"
          [class.is-invalid]="submitted && f['birthDate'].errors"
        />
        <div *ngIf="submitted && f['birthDate'].errors" class="invalid-feedback">
          Doğum tarihi gereklidir
        </div>
      </div>

      <div *ngIf="error" class="alert alert-danger">
        {{ error }}
      </div>

      <div class="form-actions">
        <button type="submit" class="btn btn-primary" [disabled]="loading">
          <span *ngIf="!loading">Kaydet</span>
          <span *ngIf="loading">Kaydediliyor...</span>
        </button>
        <button type="button" class="btn btn-secondary" (click)="goBack()">
          İptal
        </button>
      </div>
    </form>
  </div>
</div>
