import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { CommonModule } from '@angular/common';
import { PatientService } from '../../../core/services/patient.service';
import { Patient } from '../../../core/models/patient.model';

@Component({
  selector: 'app-patient-list',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './patient-list.component.html',
  styleUrls: ['./patient-list.component.css']
})
export class PatientListComponent implements OnInit {
  patients: Patient[] = [];
  loading = false;
  error = '';

  constructor(
    private patientService: PatientService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.loadPatients();
  }

  loadPatients(): void {
    this.loading = true;
    this.error = '';
    this.patientService.getAll().subscribe({
      next: (data) => {
        this.patients = data;
        this.loading = false;
      },
      error: (error: any) => {
        this.error = 'Hastalar y�klenirken hata olu�tu.';
        this.loading = false;
      }
    });
  }

  viewDetails(id: number): void {
    this.router.navigate(['/patients', id]);
  }

  createNew(): void {
    this.router.navigate(['/patients/create']);
  }

  deletePatient(id: number): void {
    if (confirm('Hastay� silmek istedi�inize emin misiniz?')) {
      this.patientService.delete(id).subscribe({
        next: () => {
          this.patients = this.patients.filter(p => p.id !== id);
        },
        error: (error: any) => {
          this.error = 'Hasta silinirken hata olu�tu.';
        }
      });
    }
  }
}
<div class="container">
  <div class="header">
    <h1>Hastalar</h1>
    <button class="btn btn-success" (click)="createNew()">+ Yeni Hasta</button>
  </div>

  <div *ngIf="error" class="alert alert-danger">
    {{ error }}
  </div>

  <div *ngIf="loading" class="loading">
    Y�kleniyor...
  </div>

  <div *ngIf="!loading && patients.length > 0" class="patients-table">
    <table>
      <thead>
        <tr>
          <th>Ad</th>
          <th>Soyad�</th>
          <th>Do�um Tarihi</th>
          <th>��lem</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let patient of patients">
          <td>{{ patient.name }}</td>
          <td>{{ patient.surname }}</td>
          <td>{{ patient.birthDate | date: 'dd.MM.yyyy' }}</td>
          <td>
            <button class="btn btn-sm btn-info" (click)="viewDetails(patient.id)">
              Detay
            </button>
            <button class="btn btn-sm btn-danger" (click)="deletePatient(patient.id)">
              Sil
            </button>
          </td>
        </tr>
      </tbody>
    </table>
  </div>

  <div *ngIf="!loading && patients.length === 0" class="empty-state">
    <p>Hasta kayd� bulunamad�.</p>
  </div>
</div>
