import { Component } from '@angular/core';
import { RouterOutlet } from '@angular/router';
import { CommonModule } from '@angular/common';
import { Router, NavigationEnd } from '@angular/router';
import { RouterLink } from '@angular/router';
import { filter } from 'rxjs';
import { AuthService } from './core/services/auth.service';

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [CommonModule, RouterOutlet, RouterLink],
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  showHeader = true;

  constructor(
    private authService: AuthService,
    private router: Router
  ) {
    this.updateHeaderVisibility(this.router.url);

    this.router.events
      .pipe(filter((e): e is NavigationEnd => e instanceof NavigationEnd))
      .subscribe(e => this.updateHeaderVisibility(e.urlAfterRedirects));
  }

  get isAuthenticated(): boolean {
    return this.authService.isAuthenticated();
  }

  onLogout(): void {
    this.authService.logout();
    this.router.navigate(['/login']);
  }

  private updateHeaderVisibility(url: string): void {
    this.showHeader = !url.startsWith('/login');
  }
}
<header *ngIf="showHeader" class="app-header">
  <div class="app-header__inner">
    <a class="app-title" routerLink="/patients">Hastane Yönetim Sistemi</a>

    <div class="app-actions">
      <button *ngIf="isAuthenticated" type="button" class="btn-logout" (click)="onLogout()">
        Logout
      </button>
    </div>
  </div>
</header>

<main class="app-main">
  <router-outlet></router-outlet>
</main>
